command:example.dev:code:
$response = Invoke-PupFetch -Page $page -Url "/api/users" -Method POST -Body @{
    username = "newuser"
    role = "admin"
} -AsJson
if ($response.Ok) { "User created: $($response.Body.id)" }

# Try accessing another user's data
$response = Invoke-PupFetch -Page $page -Url "/api/users/999/documents" -AsJson
if ($response.Ok) {
    "IDOR vulnerability! Got $($response.Body.Count) documents"
}

$response = Invoke-PupFetch -Page $page -Url "/api/account/email" -Method PUT -Body @{
    email = "attacker@evil.com"
}
"Response: $($response.Status) - Check if CSRF token was required"

$formData = Invoke-PupScript -Page $page -Script @"
() => {
    const form = document.querySelector('form');
    const data = new FormData(form);
    return Object.fromEntries(data.entries());
}
"@

$links = Find-PupElements -Page $page -Selector "a"
$links | ForEach-Object {
    $target = Get-PupElementAttribute -Element $_ -Name "target"
    $rel = Get-PupElementAttribute -Element $_ -Name "rel"
    if ($target -eq "_blank" -and $rel -notlike "*noopener*") {
        "Vulnerable link: $(Get-PupElementAttribute -Element $_ -Name 'href')"
    }
}

$global:pages = @()
Set-PupBrowserHandler -Browser $browser -Event PageCreated -ScriptBlock {
    param($e)
    $global:pages += $e.Page
    Write-Host "New page opened: $($e.Page.Url)"
}

Set-PupPageHandler -Page $page -Event Dialog -ScriptBlock {
    param($e)
    if ($e.Type -eq 'prompt') {
        $e.Accept("my answer")
    } else {
        $e.Dismiss()
    }
}

$handler = Get-PupPageHandler -Page $page -Event Dialog
if (-not $handler) {
    Set-PupPageHandler -Page $page -Event Dialog -Action Accept
}

$perm = Get-PupPermission -Page $page -Permission notifications
if ($perm.State -eq 'prompt') {
    Set-PupPermission -Page $page -Permission notifications -State Granted
}
